    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        //NUEVO PRODUCTO
        activar();
        Producto p = new Producto();
        if (validar()) {
            p.setCodigo( Integer.parseInt(txtCodigo.getText()));
            p.setDescripcion(txtDescripcion.getText());
            p.setPrecio(Double.parseDouble(txtPrecio.getText()));
            p.setStock(Integer.parseInt(txtStock.getText()));
            MenuPrincipal.listaProducto.add(p);
        } else {
            JOptionPane.showMessageDialog(this, "Por favor verifique los campos");
        }
        desacativar();

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        // GUARDAR PRODUCTO
        for (Producto aux :MenuPrincipal.listaProducto) {
            if (aux.getCodigo().equals(Integer.parseInt(txtCodigo.getText()))) {
                
                aux.setDescripcion(txtDescripcion.getText());
                aux.setPrecio(Double.parseDouble(txtPrecio.getText()));
                aux.setStock(Integer.parseInt(txtStock.getText()));
                aux.setRubro((Categoria) jComboBox1.getSelectedItem());
            }
        }
        desacativar();
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        // ELIMINAR PRODUCTO
        if (!txtCodigo.getText().isEmpty() || ! txtCodigo.getText().equals("")) {
            for (Producto aux : MenuPrincipal.listaProducto) {
                if (aux.getCodigo().equals(Integer.parseInt(txtCodigo.getText()))) {
                    MenuPrincipal.listaProducto.remove(aux);
                }
            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

