    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        // BUSCAR PRODUCTO
        if (!txtCodigo.getText().isEmpty() || ! txtCodigo.getText().equals("")) {
            for (Producto aux : MenuPrincipal.listaProducto) {
                if (aux.getCodigo().equals(Integer.parseInt(txtCodigo.getText()))) {
                    txtDescripcion.setText(aux.getDescripcion());
                    txtPrecio.setText(aux.getPrecio().toString());
                    jComboBox1.setSelectedItem(aux.getRubro());
                    txtStock.setText(aux.getStock().toString());
                    activar();
                } 
            }
        } else {
            JOptionPane.showMessageDialog(this, "Debe indicar Código");
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void activar() {
        txtCodigo.setEnabled(true);
        txtDescripcion.setEnabled(true);
        txtPrecio.setEnabled(true);
        txtStock.setEnabled(true);
    }
    private void desacativar() {
       txtCodigo.setEnabled(false);
        txtDescripcion.setEnabled(false);
        txtPrecio.setEnabled(false);
        txtStock.setEnabled(false); 
    }
    private boolean validar(){
        
        if (txtCodigo.getText().equals("")|| txtCodigo.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe indicar Código");  
        } else if (txtDescripcion.getText().equals("")|| txtDescripcion.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe indicar Descripción");
        } else if (!txtPrecio.getText().equals("")|| !txtPrecio.getText().isEmpty()) {
           JOptionPane.showMessageDialog(this, "Debe indicar Precio");
        } else if (!txtStock.getText().equals("")|| !txtStock.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe indicar Stock");
        } else {
            return true;
        }
        return false;
    }

